mapper.update:
update ecm_user_extraflow
set fk_user_id = #{fkUserId,jdbcType=INTEGER},
  extraflow_status = #{extraflowStatus,jdbcType=SMALLINT},
  extraflow_create_time = #{extraflowCreateTime,jdbcType=TIMESTAMP},
  extraflow_update_time = #{extraflowUpdateTime,jdbcType=TIMESTAMP}
where extraflow_id = #{extraflowId,jdbcType=INTEGER}

mapper.select:
    select 

    from ecm_user_extraflow
    where 1=1 and  extraflow_status = 1

	   		and extraflow_id = #{extraflowId,jdbcType=INTEGER}


	   		and fk_user_id = #{fkUserId,jdbcType=INTEGER}


	    	and extraflow_update_time = #{extraflowUpdateTime,jdbcType=TIMESTAMP}
