mapper.insert:
insert into ecm_user_extraflow (fk_user_id, extraflow_status, extraflow_start_time, 
  extraflow_end_time, extraflow_update_time
  )
values (#{fkUserId,jdbcType=INTEGER}, #{extraflowStatus,jdbcType=SMALLINT}, #{extraflowStartTime,jdbcType=TIMESTAMP}, 
  #{extraflowEndTime,jdbcType=TIMESTAMP}, #{extraflowUpdateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
    select 

    from ecm_user_extraflow
    where 1=1 and  extraflow_status = 1

	   		and extraflow_id = #{extraflowId,jdbcType=INTEGER}


	   		and fk_user_id = #{fkUserId,jdbcType=INTEGER}


	    	and #{currentDateTime} between extraflow_start_time and extraflow_end_time


	    	and extraflow_update_time = #{extraflowUpdateTime,jdbcType=TIMESTAMP}
