mapper.insert:
insert into ecm_user_flow (user_id, surplus_flow, total_flow,
  update_time, check_flow, used_flow
  )
values (#{userId,jdbcType=INTEGER}, #{surplusFlow,jdbcType=INTEGER}, #{totalFlow,jdbcType=INTEGER},
  #{updateTime,jdbcType=TIMESTAMP}, #{checkFlow,jdbcType=INTEGER}, #{usedFlow,jdbcType=INTEGER}
  )

mapper.update:
update ecm_user_flow
set
  surplus_flow = surplus_flow - #{surplusFlow},
  update_time = #{updateTime}
where user_id = #{userId}
